<?phprequire_once(LIB_PATH.DS.'database.php');class Country extends DatabaseObject {		protected static $table_name="country_region";	protected static $db_fields = array('id', 'country_abb', 'country', 'region_abb');		public $id;	public $country_abb;	public $country;	public $region_abb;	  	public function full_name() {    if(isset($this->country_abb) && isset($this->country)) {      return $this->country_abb . " " . $this->country;    } else {      return "";    }  }	public static function authenticate($country_abb="") {    global $database;    $country_abb = $database->escape_value($country_abb);    $sql  = "SELECT * FROM country_region ";    $sql .= "WHERE country_abb = '{$country_abb}' ";    $sql .= "LIMIT 1";    $result_array = self::find_by_sql($sql);		return !empty($result_array) ? array_shift($result_array) : false;	}	public static function country_sql_where($country_abb, $country, $region_abb){	$sql = "";	if($country_abb !="") {$sql .=" AND country_abb= '{$country_abb}' "; }	if($country !="") {$sql .=" AND country= '{$country}' "; }	if($region_abb !="") {$sql .=" AND region_abb= '{$region_abb}' "; }	return $sql;	}public static function country_err(){	global $database;	$select_sql = "SELECT COUNT(*) FROM country_region WHERE country_abb = '' OR country='' ";	$select_sql .= " OR region_abb NOT IN (SELECT region_code FROM region_code)";	$result_set = $database->query($select_sql);	$rows = array_shift($database->fetch_array($result_set));	if(!empty($rows)) {	return $rows; } else return '0';	}}