<?phprequire_once(LIB_PATH.DS.'database.php');class DatabaseObject {		protected static $table_name;	protected static $db_fields = array();	// I'm waiting for Late Static Bindings in PHP 5.3	// http://www.php.net/lsb	// Common Database Methods		public static function sql_where($paramarray){			$sql = " ";			if(isset($paramarray)) {				foreach($paramarray as $key => $val){					$sql .= " AND ".$key." = '".$val."' ";						}			}			return $sql;	}	  public static function count_all(){  	global $database;	$sql = "SELECT COUNT(*) FROM ".static::$table_name;	$result_set = $database->query($sql);	$row = $database->fetch_array($result_set);	return array_shift($row);	}	  public static function count_in_sql($sql){	  	global $database;	$select_sql = "SELECT COUNT(*) FROM ".static::$table_name;	if(!empty($sql)){		$select_sql .= " WHERE 1 " . $sql;		$result_set = $database->query($select_sql);		$row = $database->fetch_array($result_set);		return array_shift($row);	}	else return 0;}  public static function count_in_full_sql($sql){	  	global $database;	if($sql != ""){		$select_sql = "SELECT COUNT(*) FROM (".$sql.") z";		$result_set = $database->query($select_sql);		$row = $database->fetch_array($result_set);		return array_shift($row);	}	else return 0;}  public static function count_in_sql_all($sql){	  	global $database;		$select_sql = "SELECT COUNT(*) FROM ".static::$table_name;		$select_sql .= " WHERE 1 " . $sql;		$result_set = $database->query($select_sql);		$row = $database->fetch_array($result_set);		return array_shift($row);}		public static function find_all() {		return self::find_by_sql("SELECT * FROM ".static::$table_name);  }    public static function find_by_id($id=0) {    $result_array = self::find_by_sql("SELECT * FROM ".static::$table_name." WHERE id={$id} LIMIT 1");		return !empty($result_array) ? array_shift($result_array) : false;  }    public static function find_rec_by_id($id=0) {    $result_array = self::find_by_sql("SELECT * FROM ".static::$table_name." WHERE id={$id} LIMIT 1");		return !empty($result_array) ? $result_array : false;  }   public static function find_by_sql($sql="") {    global $database;    $result_set = $database->query($sql);    $object_array = array();    while ($row = $database->fetch_array($result_set)) {      $object_array[] = static::instantiate($row);    }    return $object_array;  }  public static function find_by_sql_lim($sql, $limit, $offset){			//		if(!empty($sql)) {		$select_sql = "SELECT * FROM " . static::$table_name . " WHERE 1 " . $sql;		$select_sql .= " LIMIT {$limit} ";			$select_sql .= " OFFSET {$offset}";		return self::find_by_sql($select_sql);//		}//		else return NULL;	}  public static function find_by_full_sql_lim($sql, $limit, $offset){			    global $database;		if(!empty($sql)) {			$select_sql =  $sql;			$select_sql .= " LIMIT {$limit} ";				$select_sql .= " OFFSET {$offset}";			$result_set = $database->query($select_sql);			$result_array = array();    		while ($row = $database->fetch_array($result_set)) {      				$result_array[] = $row;			}			return $result_array;		}		else return NULL;	}   public static function find_by_full_sql_lim_i($sql, $limit, $offset){			    global $database;		if(!empty($sql)) {			$select_sql =  $sql;			$select_sql .= " LIMIT {$limit} ";				$select_sql .= " OFFSET {$offset}";			$result_set = $database->query($select_sql);			$result_array = array();    		while ($row = $database->fetch_array($result_set)) {      				$result_array[] = static::instantiate($row);			}			return $result_array;		}		else return NULL;	}   	public static function instantiateit($record) {		// Could check that $record exists and is an array    $object = new static;//		 Simple, long-form approach:	//	 $object->id 				= $record['id'];		// $object->username 	= $record['username'];		// $object->password 	= $record['password'];		// $object->first_name = $record['first_name'];		// $object->last_name 	= $record['last_name'];				// More dynamic, short-form approach:		foreach($record as $attribute=>$value){		  if($object->has_attribute($attribute)) {		    $object->$attribute = $value;		  }		}		if(!isset($record['id'])) unset($object->id);		if($record['id'] == '') unset($object->id);		return $object;	}	private static function instantiate($record) {		// Could check that $record exists and is an array    $object = new static;		// Simple, long-form approach:		// $object->id 				= $record['id'];		// $object->username 	= $record['username'];		// $object->password 	= $record['password'];		// $object->first_name = $record['first_name'];		// $object->last_name 	= $record['last_name'];				// More dynamic, short-form approach:		foreach($record as $attribute=>$value){		  if($object->has_attribute($attribute)) {			$object->$attribute = $value;		  }		}		return $object;	}		private function has_attribute($attribute) {	  // We don't care about the value, we just want to know if the key exists	  // Will return true or false	  return array_key_exists($attribute, $this->attributes());	}	protected function attributes() {   // array of all object attributes		// return an array of attribute names and their values	  $attributes = array();	  $attributes = get_class_vars(get_called_class());//	  foreach(static::$db_fields as $field) {//	     if(property_exists($this, $field)) {//	      $attributes[$field] = $this->$field;//	    }//	  }	  return $attributes;	}		protected function dbattributes() {   // array of attributes matching database columns		// return an array of attribute names and their values	  $dbattributes = array();//	  $attributes = get_class_vars(get_called_class());	  foreach(static::$db_fields as $field) {	     if(property_exists($this, $field)) {	      $dbattributes[$field] = $this->$field;	    }	  }	  return $dbattributes;	}	protected function sanitized_attributes() {	  global $database;	  $clean_attributes = array();	  // sanitize the values before submitting	  // Note: does not alter the actual value of each attribute	  foreach($this->dbattributes() as $key => $value){	    $clean_attributes[$key] = $database->escape_value($value);	  }	  return $clean_attributes;	}		public function save() {	  // A new record won't have an id yet.	  return isset($this->id) ? $this->update() : $this->create();	}		public function create() {		global $database;		// Don't forget your SQL syntax and good habits:		// - INSERT INTO table (key, key) VALUES ('value', 'value')		// - single-quotes around all values		// - escape all values to prevent SQL injection//		echo "creating new record";		$attributes = $this->sanitized_attributes();	  $sql = "INSERT INTO ".static::$table_name." (";		$sql .= join(", ", array_keys($attributes));	  $sql .= ") VALUES ('";		$sql .= join("', '", array_values($attributes));		$sql .= "')";	  if($database->query($sql)) {	    $this->id = $database->insert_id();	    return true;	  } else {	    return false;	  }	}	public function update() {	  global $database;		// Don't forget your SQL syntax and good habits:		// - UPDATE table SET key='value', key='value' WHERE condition		// - single-quotes around all values		// - escape all values to prevent SQL injection	$attributes = $this->sanitized_attributes();		$attribute_pairs = array();		foreach($attributes as $key => $value) {		  $attribute_pairs[] = "{$key}='{$value}'";		}		$sql = "UPDATE ".static::$table_name." SET ";		$sql .= join(", ", $attribute_pairs);		$sql .= " WHERE id=". $database->escape_value($this->id);	  $database->query($sql);	  return ($database->affected_rows() == 1) ? true : false;	}	public function delete() {		global $database;		// Don't forget your SQL syntax and good habits:		// - DELETE FROM table WHERE condition LIMIT 1		// - escape all values to prevent SQL injection		// - use LIMIT 1	  $sql = "DELETE FROM ".static::$table_name;	  $sql .= " WHERE id=". $database->escape_value($this->id);	  $sql .= " LIMIT 1";	  $database->query($sql);	  return ($database->affected_rows() == 1) ? true : false;		// NB: After deleting, the instance of User still 		// exists, even though the database entry does not.		// This can be useful, as in:		//   echo $user->first_name . " was deleted";		// but, for example, we can't call $user->update() 		// after calling $user->delete().	}}?>		