<?phpfunction strip_zeros_from_date( $marked_string="" ) {  // first remove the marked zeros  $no_zeros = str_replace('*0', '', $marked_string);  // then remove any remaining marks  $cleaned_string = str_replace('*', '', $no_zeros);  return $cleaned_string;}function redirect_to( $location = NULL ) {  if ($location != NULL) {    header("Location: {$location}");    exit;  }}function output_message($message="") {  if (!empty($message)) {     return "<p class=\"message\">{$message}</p>";  } else {    return "";  }}function __autoload($class_name) {	$class_name = strtolower($class_name);  $path = LIB_PATH.DS."{$class_name}.php";  if(file_exists($path)) {    require_once($path);  } else {		die("The file {$class_name}.php could not be found.");	}}function include_layout_template($template="") {	include(SITE_ROOT.DS.'public'.DS.'layouts'.DS.$template);}function log_action($action, $message="") {	$logfile = SITE_ROOT.DS.'logs'.DS.'log.txt';	$new = file_exists($logfile) ? false : true;  if($handle = fopen($logfile, 'a')) { // append    $timestamp = strftime("%Y-%m-%d %H:%M:%S", time());		$content = "{$timestamp} | {$action}: {$message}\n";    fwrite($handle, $content);    fclose($handle);    if($new) { chmod($logfile, 0755); }  } else {    echo "Could not open log file for writing.";  }}function datetime_to_text($datetime="") {  $unixdatetime = strtotime($datetime);  return strftime("%B %d, %Y at %I:%M %p", $unixdatetime);}function backup_tables($tables = '*'){	$link = mysql_connect(DB_SERVER, DB_USER, DB_PASS);	mysql_select_db(DB_NAME,$link);	//get all of the tables	if($tables == '*')	{		$tables = array();		$result = mysql_query('SHOW TABLES');		while($row = mysql_fetch_row($result))		{			$tables[] = $row[0];		}		$tname = "full_backup";	}	else	{		$tables = is_array($tables) ? $tables : explode(',',$tables);		$tname = "_table_".$tables[0];	}		//cycle through	foreach($tables as $table)	{		$result = mysql_query('SELECT * FROM '.$table);		$num_fields = mysql_num_fields($result);				$return.= 'DROP TABLE IF EXISTS '.$table.';';		$row2 = mysql_fetch_row(mysql_query('SHOW CREATE TABLE '.$table));		$return.= "\n\n".$row2[1].";\n\n";				for ($i = 0; $i < $num_fields; $i++) 		{			while($row = mysql_fetch_row($result))			{				$return.= 'INSERT INTO '.$table.' VALUES(';				for($j=0; $j<$num_fields; $j++) 				{					$row[$j] = addslashes($row[$j]);					$row[$j] = str_replace("\n","\\n",$row[$j]);					if (isset($row[$j])) { $return.= '"'.$row[$j].'"' ; } else { $return.= '""'; }					if ($j<($num_fields-1)) { $return.= ','; }				}				$return.= ");\n";			}		}		$return.="\n\n\n";	}		//save file	$handle = fopen(SITE_ROOT.DS.'backups'.DS.'db-backup-'.$tname.date(Ymd).'.sql','w+');	fwrite($handle,$return);	fclose($handle);}