<?php// If it's going to need the database, then it's // probably smart to require it before we start.require_once(LIB_PATH.DS.'database.php');class Libraries extends DatabaseObject {		protected static $table_name="library_code";	protected static $db_fields = array('id', 'library_code', 'library');		public $id;	public $library_code;	public $library;		  public function code_name() {    if(isset($this->libray_code) && isset($this->library)) {      return $this->library_code . "  " . $this->library;    } else {      return "";    }  }public static function lib_sql_where($keycode, $keyname){						$sql = "";		if($keycode !="") {$sql .=" AND library_code= '{$keycode}' "; }		if($keyname !="") {$sql .=" AND LOCATE('{$keyname}',library) ";}	return $sql;	}	public static function is_keyword($key) {	  global $database;	$sql = "SELECT COUNT(*) FROM keywords WHERE keyword_code = '{$key}'";	$result_set = $database->query($sql);	$row = $database->fetch_array($result_set);	echo $key." ".array_shift($row);	return array_shift($row);	}		}?>