<?phprequire_once(LIB_PATH.DS.'database.php');class Region extends DatabaseObject {		protected static $table_name="region_code";	protected static $db_fields = array('id', 'region_code', 'region');		public $id;	public $region_code;	public $region;	  	public function full_name() {		if(isset($this->region_code) && isset($this->region)) {      		return $this->region_code . " " . $this->region;		} else {      return "";    }	}	public static function authenticate($region_code="") {		global $database;		$region_code = $database->escape_value($region_code);		$sql  = "SELECT * FROM region_code ";		$sql .= "WHERE region_code = '{$region_code}' ";		$sql .= "LIMIT 1";		$result_array = self::find_by_sql($sql);		return !empty($result_array) ? array_shift($result_array) : false;	}	public static function country_sql_where($state_code, $state){		$sql = "";		if($region_code !="") {$sql .=" AND region_code= '{$region_code}' "; }		if($region !="") {$sql .=" AND region= '{$region}' "; }		$sql .= " ORDER BY region ";		return $sql;	}		public static function region_err(){		global $database;		$select_sql = "SELECT COUNT(*) FROM region_code WHERE region_code = '' OR region ='' ";		$result_set = $database->query($select_sql);		$rows = array_shift($database->fetch_array($result_set));		if(!empty($rows)) {	return $rows; } else return '0';		}	}