<?php// If it's going to need the database, then it's // probably smart to require it before we start.require_once(LIB_PATH.DS.'database.php');class Sources extends DatabaseObject {		protected static $table_name="sources";	protected static $db_fields = array('id', 'source');		public $id;	public $source;	  public function code_name() {    if(isset($this->source) ) {      return $this->source;    } else {      return "";    }  }public static function sources_sql_where($source){						$sql = "";		if($source !="") {$sql .=" AND LOCATE('{$source}',source) ";}	return $sql;	}	public static function is_keyword($key) {	  global $database;	$sql = "SELECT COUNT(*) FROM keywords WHERE keyword_code = '{$key}'";	$result_set = $database->query($sql);	$row = $database->fetch_array($result_set);	echo $key." ".array_shift($row);	return array_shift($row);	}	public static function authenticate($source="") {    global $database;    $source = $database->escape_value($source);    $sql  = "SELECT * FROM sources ";    $sql .= "WHERE source = '{$source}' ";    $sql .= "LIMIT 1";    $result_array = self::find_by_sql($sql);		return !empty($result_array) ? array_shift($result_array) : false;	}	public static function source_err(){	global $database;	$select_sql = "SELECT COUNT(*) FROM sources WHERE source = ''";	$result_set = $database->query($select_sql);	$rows = array_shift($database->fetch_array($result_set));	if(!empty($rows)) {	return $rows; } else return '0';	}	}?>