<?phprequire_once(LIB_PATH.DS.'database.php');class State extends DatabaseObject {		protected static $table_name="state_code";	protected static $db_fields = array('id', 'state_code', 'state');		public $id;	public $state_code;	public $state;	  	public function full_name() {    	if(isset($this->state_code) && isset($this->state)) {      		return $this->state_code . " " . $this->state;    	} else {      return "";    }  }	public static function authenticate($state_code="") {    global $database;    $state_code = $database->escape_value($state_code);    $sql  = "SELECT * FROM state_code ";    $sql .= "WHERE state_code = '{$state_code}' ";    $sql .= "LIMIT 1";    $result_array = self::find_by_sql($sql);		return !empty($result_array) ? array_shift($result_array) : false;	}    public static function country_sql_where($state_code, $state){	$sql = "";	if($state_code !="") {$sql .=" AND state_code= '{$state_code}' "; }	if($state !="") {$sql .=" AND state= '{$state}' "; }	$sql .=" ORDER BY state ";	return $sql;	}	public static function state_err(){	global $database;	$select_sql = "SELECT COUNT(*) FROM state_code WHERE state_code = '' OR state ='' ";	$result_set = $database->query($select_sql);	$rows = array_shift($database->fetch_array($result_set));	if(!empty($rows)) {	return $rows; } else return '0';	}	}