<?phprequire_once('../includes/initialize.php');$per_page=65;$page =!empty($_GET['page']) ? (int)$_GET['page'] : 1;$refsql =!empty($_GET['sql']) ? $_GET['sql'] : "";if(isset($_POST['search'])) {	// search Form has been submitted	$isot = (isset($_POST['isot'])) ? trim($_POST['isot']) : NULL;	$source = (isset($_POST['source'])) ? trim($_POST['source']) : NULL;	$absorber = (isset($_POST['absorber'])) ? trim($_POST['absorber']) : NULL;	$refsql = Data::data_by_absorber($isot, $source, $absorber);}$total_count = Data::count_in_full_sql($refsql);$pagination = new Pagination($page, $per_page, $total_count);$found_records = Data::find_by_full_sql_lim($refsql, $per_page, $pagination->offset());?><?php include_layout_template('header.php'); ?><div id="leftcol"><h3>Data by absorber</h3>		<form action="a_study_by_absorber.php" method="post">		  <table>		    <tr>		      <td>Isotope code:</td>		      <td>		        <input type="text" name="isot" size="4" maxlength="3" value="<?php echo $isot; ?>" />		      </td>		    </tr>		    <tr>		      <td>Source:</td>		      <td>		        <input type="text" name="source" size="10" maxlength="15" value="<?php echo $source; ?>" />		      </td>		    </tr>		    <tr>		      <td>Absorber:</td>		      <td>		        <input type="text" name="absorber" size="10" maxlength="15" value="<?php echo $absorber; ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="search" value="Get data" />				</form>			   				</td>    		</tr>		  </table><?php 	$current_page = $pagination->next_page()-1;	echo "Found - ". $total_count . "<br/>Page ". $current_page ." out of ". $pagination->total_pages() . "<br/>";	if($pagination->total_pages() > 1) {			if($pagination->has_previous_page()){			echo " <a href=\"a_study_by_keyword.php?page=";			echo $pagination->previous_page();			echo "&sql=".$refsql."\">&laquo; Previous</a> ";		}			if($pagination->has_next_page()){			echo " <a href=\"a_study_by_keyword.php?page=";			echo $pagination->next_page();			echo "&sql=".$refsql."\">Next &raquo; </a> ";		}		}?></div>		<div id="data"><?php //echo $message."</br>";if(isset($found_records))	foreach($found_records as $record){		echo "<table>				<tr>		<td width=650>".$record[0]." ".$record[1]." ".$record[2]." ".$record[3]." ".$record[4]." ".		$record[5]." ".$record[6]." ".$record[7]." ".$record[8]." ".$record[9]." ".$record[10]."</td>				</tr>			</table>";		}?></div><?php include_layout_template('footer.php'); ?>		