<?phprequire_once('../includes/initialize.php');//if (!$session->is_admin()) { redirect_to("../index.php"); }$tablename = "Authors";if(isset($_GET['new'])) {	// new Form has been submitted  $entry['ref_key'] = strtoupper(trim($_GET['ref_key']));  $entry['medi_num'] = trim($_GET['medi_num']);  $entry['author_key'] = strtoupper(trim($_GET['author_key']));  $entry['author_position'] = trim($_GET['author_position']);  $entry['cauthor_flag'] = strtoupper(trim($_GET['cauthor_flag'])); $id="";   if(!$session->allow('enter')) $message = "Can't ";  $message .= "Create new record in " . $tablename . " table"; }if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id']; 	$current_record = Authors::find_rec_by_id($id);	$message = "Editing record " . $id . " of " . $tablename . " table";}if (isset($_POST['save'])) { // save Form has been submitted.  $entry['ref_key'] = strtoupper(trim($_POST['ref_key']));  $entry['medi_num'] = trim($_POST['medi_num']);  $entry['author_key'] = strtoupper(trim($_POST['author_key']));  $entry['author_position'] = trim($_POST['author_position']);  $entry['cauthor_flag'] = strtoupper(trim($_POST['cauthor_flag']));	if($_POST['id'] != "" && $session->allow('update'))  {	// update existing record		$entry['id'] = $_POST['id'];	  	$current_record[0] = Authors::instantiateit($entry);			if(Authors::authenticate($current_record[0]->ref_key,$current_record[0]->author_key)->id == $current_record[0]->id) { // update record			$current_record[0]->save();				$message = "Record updated in " . $tablename . " table";		}			else $message = "can't update record";				}	else {	// create new record	    if($session->allow('enter')){		unset($entry['id']);  		$current_record[0] = Authors::instantiateit($entry);				if(Authors::authenticate($current_record[0]->ref_key,$current_record[0]->author_key))	$message = "Code already exists in " . $tablename . " table";		else {				$current_record[0]->save();			$message = "Record createded in " . $tablename . " table";		}	    }	  else $message = "Can't create new record. No create privilege";	    	}}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new Authors;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);	}	else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editauthor.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Ref Key:</td>		      <td>		        <input type="text" name="ref_key" maxlength="6" value="<?php echo htmlentities($current_record[0]->ref_key); ?>" />		      </td>		    </tr>		    <tr>		      <td>MEDI number:</td>		      <td>		        <input type="text" name="medi_num" maxlength="4" value="<?php echo htmlentities($current_record[0]->medi_num); ?>" />		      </td>		    </tr>		    <tr>		      <td>Author Key:</td>		      <td>		        <input type="text" name="author_key" maxlength="6" value="<?php echo htmlentities($current_record[0]->author_key); ?>" />		      </td>		    </tr>		    <tr>		      <td>Author Position:</td>		      <td>		        <input type="text" name="author_position" maxlength="3" value="<?php echo htmlentities($current_record[0]->author_position); ?>" />		      </td>		    </tr>		    <tr>		      <td>Corresponding Flag:</td>		      <td>		        <input type="text" name="cauthor_flag" maxlength="1" value="<?php echo htmlentities($current_record[0]->cauthor_flag); ?>" />		      </td>		    </tr>		    <tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" 