<?phprequire_once('../includes/initialize.php');//if (!$session->is_admin()) { redirect_to("../index.php"); }$tablename = "Flags";if(isset($_GET['new'])) {	// new Form has been submitted  $entry['flag_abb'] = strtoupper(trim($_GET['flag_abb']));  $entry['definition'] = trim($_GET['definition']);  $id="";    if(!$session->allow('enter')) $message = "Can't ";  $message .= "Create new record in " . $tablename . " table"; }if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id']; 	$current_record = Flags::find_rec_by_id($id);	$message = "Editing record " . $id . " of " . $tablename . " table";}if (isset($_POST['save'])) { // save Form has been submitted.  	$entry['flag_abb'] = strtoupper(trim($_POST['flag_abb']));  	$entry['definition'] = trim($_POST['definition']); 	if($_POST['id'] != "" && $session->allow('update'))  {	// update existing record		$entry['id'] = $_POST['id'];	  	$current_record[0] = Flags::instantiateit($entry);			if(Flags::authenticate($current_record[0]->flag_abb)->id == $current_record[0]->id) { // update record			$current_record[0]->save();				$message = "Record updated in " . $tablename . " table";		}			else $message = "can't update record";				}	else {	// create new record	    if($session->allow('enter')){		unset($entry['id']);  		$current_record[0] = Flags::instantiateit($entry);				if(Flags::authenticate($current_record[0]->flag_abb))	$message = "Code already exists in " . $tablename . " table";		else {				$current_record[0]->save();			$message = "Record createded in " . $tablename . " table";		}			}	else $message = "Can't create new record. No create privilege";      }}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new Flags;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);	}	else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editflag.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Flag abb:</td>		      <td>		        <input type="text" name="flag_abb" maxlength="1" value="<?php echo htmlentities($current_record[0]->flag_abb); ?>" />		      </td>		    </tr>		    <tr>		      <td>Definition:</td>		      <td>		        <input type="text" name="definition" maxlength="100" value="<?php echo htmlentities($current_record[0]->definition); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form>		<a href="listflags.php">Done</a><?php include_layout_template('admin_footer.php'); ?>		