<?phprequire_once('../includes/initialize.php');//if (!$session->is_admin()) { redirect_to("../index.php"); }if(isset($_POST['new'])) {	// new Form has been submitted	$message = "Creating new record ";}if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id'];	$current_record = Flag::find_rec_by_id($id);	$message = "Editing ".$id;}if (isset($_POST['save'])) { // save Form has been submitted.  $entry['flag_abb'] = trim($_POST['flag_abb']);  $entry['definition'] = trim($_POST['definition']);  if(isset($_POST['id']))  $entry['id'] = $_POST['id']; else unset($entry['id']);  $newrecord = Flag::instantiateit($entry);	  if($_POST['id'] == "") unset($newrecord->id);  $newrecord->save();  $message = "User ".$newrecord->full_name. " is saved.";  unset($id);}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "") {		$delrecord = new Flag;		$delrecord->id = $_POST['id'];		$message = "User ".$delrecord->full_name. " is deleted.";		$delrecord->delete();		unset($id);	}}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editflagcode.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Flag code:</td>		      <td>		        <input type="text" name="flag_abb" maxlength="30" value="<?php echo htmlentities($current_record[0]->country_abb); ?>" />		      </td>		    </tr>		    <tr>		      <td>Definition:</td>		      <td>		        <input type="text" name="definition" maxlength="30" value="<?php echo htmlentities($current_record[0]->country); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form><?php include_layout_template('admin_footer.php'); ?>		