";		}			}}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new Isotopes;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);	}	else $message = "Can't create new record. No create privilege";    }    else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editisotopecode.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Isotope Code:</td>		      <td>		        <input type="text" name="isotope_code" maxlength="3" value="<?php echo htmlentities($current_record[0]->isotope_code); ?>" />		      </td>		    </tr>		    <tr>		      <td>Topic Abb:</td>		      <td>		        <input type="text" name="topic_abb" maxlength="6" value="<?php echo htmlentities($current_record[0]->topic_abb); ?>" />		      </td>		    </tr>		    <tr>		      <td>Subject Code:</td>		      <td>		        <input type="text" name="subject_code" maxlength="2" value="<?php echo htmlentities($current_record[0]->subject_code); ?>" />		      </td>		    </tr>		    <tr>		      <td>Energy:</td>		      <td>		        <input type="text" name="energy" maxlength="10" value="<?php echo htmlentities($current_record[0]->energy); ?>" />		      </td>		    </tr>		    <tr>		      <td>Subject Flag:</td>		      <td>		        <input type="text" name="subject_flag" maxlength="1" value="<?php echo htmlentities($current_record[0]->subject_flag); ?>" />		      </td>		    </tr>		    <tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form>		<a href="listisotopes.php">Done</a><?php include_layout_template('admin_footer.php'); ?>		