<?phprequire_once('../includes/initialize.php');$tablename = "Keywords";if(isset($_GET['new'])) {	// new Form has been submitted  $entry['ref_key'] = strtoupper(trim($_GET['ref_key']));  $entry['data_key'] = trim($_GET['data_key']);  $entry['keyword_code'] = strtoupper(trim($_GET['keyword_code']));  $id="";    if(!$session->allow('enter')) $message = "Can't ";  $message .= "Create new record in " . $tablename . " table"; }if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id']; 	$current_record = Keywords::find_rec_by_id($id);	$message = "Editing record " . $id . " of " . $tablename . " table";}if (isset($_POST['save'])) { // save Form has been submitted.  	$entry['ref_key'] = strtoupper(trim($_POST['ref_key']));  	$entry['data_key'] = trim($_POST['data_key']);	$entry['keyword_code'] = strtoupper(trim($_POST['keyword_code']));	if($_POST['id'] != "" && $session->allow('update'))  {	// update existing record		$entry['id'] = $_POST['id'];	  	$current_record[0] = Keywords::instantiateit($entry);			if(Keywords::authenticate($current_record[0]->ref_key,$current_record[0]->data_key)->id == $current_record[0]->id) { // update record			$current_record[0]->save();				$message = "Record updated in " . $tablename . " table";		}			else $message = "can't update record";				}	else {	// create new record	    if($session->allow('enter')){		unset($entry['id']);  		$current_record[0] = Keywords::instantiateit($entry);				if(Keywords::authenticate($current_record[0]->ref_key))	$message = "Code already exists in " . $tablename . " table";		else {				$current_record[0]->save();			$message = "Record createded in " . $tablename . " table";		}			}	  else $message = "Can't create new record. No create privilege";	}}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new Keywords;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);	}       else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editkeyword.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Ref key:</td>		      <td>		        <input type="text" name="ref_key" maxlength="6" value="<?php echo htmlentities($current_record[0]->ref_key); ?>" />		      </td>		    </tr>		    <tr>		      <td>Data key:</td>		      <td>		        <input type="text" name="data_key" maxlength="3" value="<?php echo htmlentities($current_record[0]->data_key); ?>" />		      </td>		    </tr>		    <tr>		      <td>Keyword code:</td>		      <td>		        <input type="text" name="keyword_code" maxlength="3" value="<?php echo htmlentities($current_record[0]->keyword_code); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form>		<a href="listkeywords.php">Done</a><?php include_layout_template('admin_footer.php'); ?>		