<?phprequire_once('../includes/initialize.php');$tablename = "Libraries";if(isset($_GET['new'])) {	// new Form has been submitted  $entry['library_code'] = strtoupper(trim($_GET['library_code']));  $entry['library'] = trim($_GET['library']);  $id="";  if(!$session->allow('enter')) $message = "Can't ";  $message .= "Create new record in " . $tablename . " table"; }if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id']; 	$current_record = Libraries::find_rec_by_id($id);	$message = "Editing record " . $id . " of " . $tablename . " table";}if (isset($_POST['save'])) { // save Form has been submitted.  	$entry['library_code'] = strtoupper(trim($_POST['library_code']));  	$entry['library'] = trim($_POST['library']); 	if($_POST['id'] != "" && $session->allow('update'))  {	// update existing record		$entry['id'] = $_POST['id'];	  	$current_record[0] = Libraries::instantiateit($entry);			if(Libraries::authenticate($current_record[0]->library_code)->id == $current_record[0]->id) { // update record			$current_record[0]->save();				$message = "Record updated in " . $tablename . " table";		}			else $message = "can't update record";				}	else {	// create new record	  if($session->allow('enter')){		unset($entry['id']);  		$current_record[0] = Libraries::instantiateit($entry);				if(Libraries::authenticate($current_record[0]->library_code))	$message = "Code already exists in " . $tablename . " table";		else {				$current_record[0]->save();			$message = "Record createded in " . $tablename . " table";		}	  }	  else $message = "Can't create new record. No create privilege";	}}if (isset($_POST['delete'])) { // delete Form has been submitted.    if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new Libraries;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);    }    else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editlibrary.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $current_record[0]->id; ?>" />		    <tr>		      <td>Library code:</td>		      <td>		        <input type="text" name="library_code" maxlength="30" value="<?php echo htmlentities($current_record[0]->library_code); ?>" />		      </td>		    </tr>		    <tr>		      <td>Library:</td>		      <td>		        <input type="text" name="library" maxlength="30" value="<?php echo htmlentities($current_record[0]->library); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form>		<a href="listlibraries.php">Done</a><?php include_layout_template('admin_footer.php'); ?>		