<?phprequire_once('../includes/initialize.php');$tablename = "Regions";if(isset($_GET['new'])) {	// new Form has been submitted  $entry['region_code'] = strtoupper(trim($_GET['region_code']));  $entry['region'] = trim($_GET['region']);  $id="";   if(!$session->allow('enter')) $message = "Can't ";  $message .= "Create new record in " . $tablename . " table"; }if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id'];	$current_record = Region::find_rec_by_id($id);	$message = "Editing record " . $id . " of " . $tablename . " table";}if (isset($_POST['save'])) { // save Form has been submitted.  	$entry['region_code'] = strtoupper(trim($_POST['region_code']));  	$entry['region'] = trim($_POST['region']); 	if($_POST['id'] != "" && $session->allow('update'))  {	// update existing record		$entry['id'] = $_POST['id'];	  	$current_record[0] = Region::instantiateit($entry);			if(Region::authenticate($current_record[0]->region_code)->id == $current_record[0]->id) { // update record			$current_record[0]->save();				$message = "Record updated in " . $tablename . " table";		}			else $message = "can't update record";				}	else {	// create new record	  if($session->allow('enter')){	    unset($entry['id']);  		$current_record[0] = Region::instantiateit($entry);				if(Region::authenticate($current_record[0]->region_code))	$message = "Code already exists";		else {				$current_record[0]->save();			$message = "New record createded in " . $tablename . " table";		}			  }	  else $message = "Can't create new record. No create privilege";	}}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new Region;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);	}    else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editregion.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Code:</td>		      <td>		        <input type="text" name="region_code" maxlength="30" value="<?php echo htmlentities($current_record[0]->region_code); ?>" />		      </td>		    </tr>		    <tr>		      <td>Region:</td>		      <td>		        <input type="text" name="region" maxlength="30" value="<?php echo htmlentities($current_record[0]->region); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form>		<a href="listregions.php">Done</a><?php include_layout_template('admin_footer.php'); ?>		