<?phprequire_once('../includes/initialize.php');//if (!$session->is_admin()) { redirect_to("../index.php"); }$tablename = "States";if(isset($_GET['new'])) {	// new Form has been submitted  $entry['state_code'] = strtoupper(trim($_GET['state_code']));  $entry['state'] = trim($_GET['state']);  $id="";    if(!$session->allow('enter')) $message = "Can't ";  $message .= "Create new record in " . $tablename . " table"; }if (isset($_POST['edit'])) { // edit Form has been submitted.	$id = $_POST['id']; 	$current_record = State::find_rec_by_id($id);	$message = "Editing record " . $id . " of " . $tablename . " table";}if (isset($_POST['save'])) { // save Form has been submitted.  	$entry['state_code'] = strtoupper(trim($_POST['state_code']));  	$entry['state'] = trim($_POST['state']); 	if($_POST['id'] != "" && $session->allow('update'))  {	// update existing record		$entry['id'] = $_POST['id'];	  	$current_record[0] = State::instantiateit($entry);			if(State::authenticate($current_record[0]->state_code)->id == $current_record[0]->id) { // update record			$current_record[0]->save();				$message = "Record updated in " . $tablename . " table";		}			else $message = "can't change primary code";				}	else {	// create new record	    if($session->allow('enter')){		unset($entry['id']);  		$current_record[0] = State::instantiateit($entry);				if(State::authenticate($current_record[0]->state_code))	$message = "Code already exists in " . $tablename . " table";		else {				$current_record[0]->save();			$message = "Record createded in " . $tablename . " table";		}			}	  else $message = "Can't create new record. No create privilege";	}}if (isset($_POST['delete'])) { // delete Form has been submitted.	if($_POST['id'] != "" && $session->allow('delete')) {		$delrecord = new State;		$delrecord->id = $_POST['id'];		$message = "Record ".$delrecord->full_name. " is deleted from " . $tablename . " table.";		$delrecord->delete();		unset($id);	}    else $message = "Record is not deleted. No delete privilege";}?><?php include_layout_template('header.php'); ?><?php echo output_message($message); ?>		<form action="editstate.php" method="post">		  <table>		        <input type="hidden" name="id"  value="<?php echo $id; ?>" />		    <tr>		      <td>Code:</td>		      <td>		        <input type="text" name="state_code" maxlength="30" value="<?php echo htmlentities($current_record[0]->state_code); ?>" />		      </td>		    </tr>		    <tr>		      <td>State:</td>		      <td>		        <input type="text" name="state" maxlength="30" value="<?php echo htmlentities($current_record[0]->state); ?>" />		      </td>		    </tr>		    <tr>		      <td colspan="1">		        <input type="submit" name="save" value="Save" />		      </td>			  <td colspan="1">		        <input type="submit" name="delete" value="Delete" />		      </td>		    </tr>		  </table>		</form>		<a href="liststates.php">Done</a><?php include_layout_template('admin_footer.php'); ?>		