($_POST['saveisotope'])) {	// save isotope Form has been submitted	$found_records = Isotopes::compose_isotope_list();	$handle = fopen('..' . DS . 'outputs' . DS . "FMIsotopes", "w");	//fwrite($handle, Isotopes::export_isotope_list($found_records));//change by jiehou	fwrite($handle, addslashes(Isotopes::export_isotope_list($found_records)));	fclose($handle);	$message = "Saved isotopelist in the file FMIsotopes";}if(isset($_POST['printrefs'])) {	// search refs Form has been submitted	$found_records = References::compose_issue_refs($medi);	foreach($found_records as $record) $record->add_definitions();	$message = "Searched references for ".$medi;}if(isset($_POST['saverefs'])) {	// save refs Form has been submitted	$found_records = References::compose_issue_refs($medi);	foreach($found_records as $record) $record->add_definitions();	$handle = fopen('..' . DS . 'outputs' . DS ."FMrefs". $medi, "w");	foreach($found_records as $record){		//fwrite($handle, $record->export_ref());//change by jiehou		fwrite($handle, addslashes($record->export_ref()));	}	fclose($handle);	$message = "Saved references in the file FMrefs".$medi;}if(isset($_POST['printlang'])) {	// search subject Form has been submitted	$found_records = Languages::compose_language_list();	$message = "Searched languages for language list";}if(isset($_POST['savelang'])) {	// save subject Form has been submitted	$found_records = Languages::compose_language_list();	$handle = fopen('..' . DS . 'outputs' . DS . "FMLanguages", "w");	//fwrite($handle, Languages::export_lang_list($found_records));//change by jiehou	fwrite($handle, addslashes(Languages::export_lang_list($found_records)));	fclose($handle);	$message = "Saved languages in the file FMLanguages";}if(isset($_POST['printkeycode'])) {	// search authors Form has been submitted	$found_records = Keycodes::compose_keycode_list();	$message = "Searched authors for ".$medi;}if(isset($_POST['savekeycode'])) {		// save authors Form has been submitted		        	$found_records = Keycodes::compose_keycode_list();		$handle = fopen('..' . DS . 'outputs' . DS . "FMKeywordcodes", "w");	//fwrite($handle, Keycodes::export_keycode_list($found_records));//change by jiehou	fwrite($handle, addslashes(Keycodes::export_keycode_list($found_records)));	fclose($handle);	if(get_magic_quotes_gpc()==false){		$fileContent=mysql_escape_string($fileContent);	}	$message = "Saved keywordcodes in the file FMKeywordcodes";}?><?php include_layout_template('header.php'); ?><div id="leftcol"><h3>Print Issue</h3>		<form action="exportissue.php" method="post">			<input type="hidden" name="medi" value="<?php echo $medi; ?>" />		  <table>		    <tr>		      <td>MERDJ #:</td>		      <td>		        <input type="text" name="medi" size="6" maxlength="4" value="<?php echo $medi; ?>" />		      </td>		    </tr>			<tr>		      <td >		        <input type="submit" name="printabbr" value="Print abbr     " />			  </td>			  <td >		        <input type="submit" name="saveabbr" value="Save abbr     " />			  </td>    		</tr>			<tr>		      <td >		        <input type="submit" name="printdata" value="Print data     " />			  </td>			  <td >		        <input type="submit" name="savedata" value="Save data     " />			  </td>    		</tr>			<tr>		      <td >		        <input type="submit" name="printisotope" value="Print isotops" />			  </td>			  <td >		        <input type="submit" name="saveisotope" value="Save isotops" />			  </td>    		</tr>			<tr>		      <td >		        <input type="submit" name="printrefs" value="Print refs      " />			  </td>			  <td >		        <input type="submit" name="saverefs" value="Save refs      " />			  </td>    		</tr>			<tr>		      <td >		        <input type="submit" name="printlang" value="Print lang     " />			  </td>			  <td >		        <input type="submit" name="savelang" value="Save lang     " />			  </td>    		</tr>    		</tr>			<tr>		      <td >		      