<?phprequire_once('../includes/initialize.php');//if (!$session->is_admin()) { redirect_to("../index.php"); }$per_page=20;$page =!empty($_GET['page']) ? (int)$_GET['page'] : 1;$refsql =!empty($_GET['sql']) ? $_GET['sql'] : "ORDER BY isotope_code";if(isset($_POST['search'])) {	// search Form has been submitted  $entry['isotope_code'] = trim($_POST['isotope_code']);  $entry['element'] = trim($_POST['element']);    $entry['isotope'] = trim($_POST['isotope']); 	$refsql = Isotopes::iso_sql_where($entry['isotope_code'],$entry['isotope'],$entry['element']);	$message = "Searched ".$refsql;}$total_count = Isotopes::count_in_sql_all($refsql);$pagination = new Pagination($page, $per_page, $total_count);$found_records = Isotopes::find_by_sql_lim($refsql, $per_page, $pagination->offset());//print_r($found_records);?><?php include_layout_template('header.php'); ?><div id="leftcol">		<form action="listisotopes.php" method="post">		  <table>		    <tr>		      <td>Isotope code:</td>		      <td>		        <input type="text" name="isotope_code" size="12" maxlength="3" value="" />		      </td>		    </tr>		    <tr>		      <td>Element:</td>		      <td>		        <input type="text" name="element" size="12" maxlength="2" value="" />		      </td>		    </tr>		    <tr>		      <td>Isotope:</td>		      <td>		        <input type="text" name="isotope" size="12" maxlength="6" value="" />		      </td>		    </tr>		    <tr>		      <td colspan="2">		        <input type="submit" name="search" value="Search" />		      </td>	    </tr>		  </table>		</form><?php 	$current_page = $pagination->next_page()-1;	echo "Found - ". $total_count . "<br/>Page ". $current_page ." out of ". $pagination->total_pages() . "<br/>";	if($pagination->total_pages() > 1) {			if($pagination->has_previous_page()){			echo " <a href=\"listisotopes.php?page=";			echo $pagination->previous_page();			echo "&sql=".$refsql."\">&laquo; Previous</a> ";		}			if($pagination->has_next_page()){			echo " <a href=\"listisotopes.php?page=";			echo $pagination->next_page();			echo "&sql=".$refsql."\">Next &raquo; </a> ";		}		}?></div>		<div id="data"><?php echo $message."</br>";foreach($found_records as $record){		echo "<form action='editisotopecode.php' method='post'>			<table>				<tr>					<td width=350>".$record->full_name()."</td>						<td>						<input type='hidden' name='id' value=".						$record->id.">						<input type='submit' name='edit' value='Edit'>					</td>				</tr>			</table>			</form>";		}?></div><?php include_layout_template('footer.php'); ?>		